const User =()=>{

  const motorOn = (e)=>{
    fetch("/motoron").then(res=>{
      console.log(res)
    }).catch(err=>{
      console.log(err)
    })
  }


  return(
    <>
    <div className="align-item-centre">
      <h1>Progress Bar</h1>
    <ProgressBar>
      <ProgressBar striped variant="success" now={35} key={1} />
      <ProgressBar variant="warning" now={20} key={2} />
      <ProgressBar striped variant="danger" now={10} key={3} />
    </ProgressBar>
    <br></br><br></br>
    <div className="mx-auto">
    <div className="container-fluid nav-bg">
      <div className="row">
      <div className="col-1 mx-auto">
    <div className="btn-group" role="group" aria-label="Button group with nested dropdown">
  <button type="button" className="btn btn-outline-primary">Automate</button>

  <div className="btn-group" role="group">
    <button id="btnGroupDrop1" type="button" className="btn btn-outline-warning dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
      Manual
    </button>
    <ul className="dropdown-menu" aria-labelledby="btnGroupDrop1">
      <li><button onClick={motorOn} type="button" className="btn btn-outline-success">Start Moter</button></li>
      <li><button type="button" className="btn btn-outline-danger">Stop Moter</button></li>
    </ul>
  </div>
</div>
</div>
</div>
</div>
    </div>



    </div>
    </>
      );
};

export default User;
